import React, { Component } from 'react'

export default class Slider extends Component {
    constructor() {
        super()

        this.state = {
            position: this.props.posInit
        }
    }
    render() {
        const pos = this.props.renderPos
        return (
            <div className={`card card-order-${pos} card--${pos}`}
                key={this.props.key}
                onClick={() => this.props.onClick(this.props.renderPos)}
            >
                <img 
                    src={this.props.info.img_src} 
                    alt="" 
                />
            </div>
        )
    }
}
