import React, { Component } from 'react'
import Card from './Card.jsx'

export default class Slider extends Component {
    constructor(props) {
        super(props) 
        this.state = {
            list: [...this.props.list],
            center: Math.floor(this.props.list.length/2),
        }
    }
    handleClick(n) {
        this.setState({center: n})
    }
    render() {
        const cards = this.state.list.map((card, i) => (   
                <Card 
                    key={card.key}
                    info={card}
                    initialPosition={i}
                >

                </Card>
          ))

        return (
            <div className="slider">        
                {cards}
            </div>
        )
    }
}
