import React, { Component } from 'react'
import { CSSTransitionGroup } from 'react-transition-group'
import Card from './Card.jsx'

export default class Slider extends Component {
    constructor(props) {
        super(props) 
        this.state = {
            list: [...this.props.list],
            center: Math.floor(this.props.list.length/2)
        }
    }
    handleClick(renderPos) {
        console.log('xd')
        const newList = this.state.list.slice()
        if(renderPos === 'right') {
            newList.push(newList.shift())
        } else if (renderPos === 'left') {
            newList.unshift(newList.pop())
        }
        this.setState({list: newList})
    }
    checkPosition(center, i) {
        return  i > center ?
                        'right'
                        : i < center ?
                            'left'
                            : 'center'

    }
    render() {
        const cards = this.state.list.map((card, i) => (   
                <Card 
                    key={card.key}
                    info={card}
                    renderPos={this.checkPosition(this.state.center, i)} 
                    onClick={(i) => this.handleClick.bind(this).bind(i)}>
                </Card>
          ));

        return (
            <div>
                <CSSTransitionGroup
                    className="slider"
                    transitionName="example"
                    transitionEnterTimeout={500}
                    transitionLeaveTimeout={300}
                >        
                        {cards}
                </CSSTransitionGroup>
            </div>
        )
    }
}
