import React, { Component } from 'react'
import Card from './Card.jsx'

export default class Slider extends Component {
    constructor(props) {
        super(props) 
        this.positionList = [...this.props.list].map((e, i) => i)
        this.state = {
            list: [...this.props.list],
            center: Math.floor(this.props.list.length/2),
        }
    }
    handleClick(prevPosition) {
        // Check if clicked center card
        const center = this.state.center
        if(prevPosition === center) return ;

        const shift = Math.abs(center - prevPosition)
        const isSlideRight = center > prevPosition ? false : true
        const newPosArr = this.state.posArr.slice()

        if(isSlideRight) {
            for (let i = 0; i < shift; i++) { 
                console.log(this.state.posArr)
                newPosArr.push(newPosArr.shift())
            } 
        } else {
            for (let i = 0; i < shift; i++) {
                newPosArr.unshift(newPosArr.pop())
            }             
        }
        this.setState({posArr: newPosArr})

    }
    rePosition() {

    }
    render() {
        const cards = this.state.list.map((card, i) => (   
                <Card 
                    key={card.key}
                    info={card}
                    // isCenter={this.state.center === checkPosition}
                    position={this.state.posArr.indexOf(i)} 
                    onClick={this.handleClick.bind(this)}
                >

                </Card>
          ))

        return (
            <div className="slider">        
                {cards}
            </div>
        )
    }
}
